class MainScreenActivity : AppCompatActivity() {
	lateinit var db: Database
	lateinit var binding: ActivityMainScreenBinding
	var characterId by Delegates.notNull<Int>()
	lateinit var itemTableDao: ItemTableDao
	lateinit var cubeTableDao: CubeTableDao
	lateinit var itemsDao: ItemsDao
	lateinit var itemTables: List<ItemTable>
	lateinit var cubeTables: List<CubeTable>
	lateinit var items: List<Items>
	@SuppressLint("Recycle")
	override fun onCreate(savedInstanceState: Bundle?) {
		super.onCreate(savedInstanceState)
		binding = ActivityMainScreenBinding.inflate(layoutInflater)
		setContentView(binding.root)

		characterId = intent.getIntExtra("characterId", 0)

		db = Database.getInstance(applicationContext)!! // 데이터베이스

		runBlocking {
			CoroutineScope(Dispatchers.IO).launch {
				itemTableDao = db.itemTableDao()
				itemTables = itemTableDao.getAll()
				cubeTableDao = db.cubeTableDao()
				cubeTables = cubeTableDao.getAll()
			}.join()
		}
		if(itemTables.isEmpty())
			createItemTable()
		if(cubeTables.isEmpty()) {
			runBlocking {
				CoroutineScope(Dispatchers.IO).launch {
					itemsDao = db.itemsDao()
					items = itemsDao.getAll()
				}.join()
			}
			for(item in items) {
				val downAbility = item.downAbilityList.toMutableList()
				if(item.itemLvl >= 201) {
					for(i in 2..4) {
						when(downAbility[i]%100) {
								1, 3, 5, 7 ->
									if(downAbility[i]/100 == 6 || downAbility[i]/100 == 8)
										downAbility[i] += 100
								13 ->
									if(downAbility[i]/100 == 5 || (downAbility[i]/100 == 6 && i == 2))
										downAbility[i] += 100
						}
					}
				} else {
					for(i in 2..4) {
						when(downAbility[i]%100) {
							1, 3, 5, 7 ->
								if(downAbility[i]/100 == 5 || downAbility[i]/100 == 7)
									downAbility[i] += 100
							13 ->
								if(downAbility[i]/100 == 4 || (downAbility[i]/100 == 5 && i == 2))
									downAbility[i] += 100
						}
					}
				}
				runBlocking {
					CoroutineScope(Dispatchers.IO).launch {
						itemsDao.updateDownAbility(downAbility, item.id)
					}
				}
			}

			createCubeTable()
		}

		val requestLauncher: ActivityResultLauncher<Intent> = registerForActivityResult(
			ActivityResultContracts.StartActivityForResult()
		) {

		}

		binding.mainScreenToStatueBtn.setOnClickListener {
			val intent: Intent = Intent(this, StatusActivity::class.java)
			intent.putExtra("characterId", characterId)
			requestLauncher.launch(intent)
		}

		binding.mainScreenToEquipmentBtn.setOnClickListener {
			val intent: Intent = Intent(this, EquipmentActivity::class.java)
			intent.putExtra("characterId", characterId)
			requestLauncher.launch(intent)
		}

		binding.mainScreenToInventoryBtn.setOnClickListener {
			val intent: Intent = Intent(this, InventoryActivity::class.java)
			intent.putExtra("characterId", characterId)
			requestLauncher.launch(intent)
		}

		binding.mainScreenToBossSelectBtn.setOnClickListener {
			val intent = Intent(this, BossSelectActivity::class.java)
			intent.putExtra("characterId", characterId)
			requestLauncher.launch(intent)
		}
	}

	private val newItemTables = emptyList<ItemTable>().toMutableList()

	private fun createITOT(id: Int,  itemSetCode: Int, itemType: Int, itemLvl: Int, name:String, str: Int, dex: Int, int: Int, luk: Int, atk: Int, spl: Int,
												 armIgn: Double, maxHp: Int, armor: Int, upGrade: Int,
												 canCube: Int, canStarForce: Int, maxStar: Int, canFlame: Int, canEnchant: Int, canCreate: Int) {
		val baseStat = MutableList<Int>(100){0}
		baseStat[0] = str; baseStat[2] = dex; baseStat[4] = int; baseStat[6] = luk
		baseStat[8] = atk; baseStat[10] = spl; baseStat[20] = maxHp; baseStat[24] = armor

		val newItemTable = ItemTable(id, itemSetCode, itemType, itemLvl, name,
			baseStat.toList(), armIgn, upGrade,
			canCube, canStarForce, maxStar, canFlame, canEnchant, canCreate)
		newItemTables.add(newItemTable)
	}

	private fun createITOT_minor(id: Int,  itemSetCode: Int, itemType: Int, itemLvl: Int, name:String, str: Int, dex: Int, int: Int, luk: Int, atk: Int, spl: Int,
												 jmp: Int, spd: Int, armIgn: Double, dmg: Int, bdmg: Int,
												 maxHp: Int, maxHpPer: Int, maxMp: Int, maxMpPer: Int, armor: Int,
												 meso: Int, drop: Int, upGrade: Int,
												 canCube: Int, canStarForce: Int, maxStar: Int, canFlame: Int, canEnchant: Int, canCreate: Int) {
		var baseStat = MutableList<Int>(100){0}
		baseStat[0] = str; baseStat[2] = dex; baseStat[4] = int; baseStat[6] = luk
		baseStat[8] = atk; baseStat[10] = spl; baseStat[14] = jmp; baseStat[15] = spd
		baseStat[18] = dmg; baseStat[19] = bdmg
		baseStat[20] = maxHp; baseStat[21] = maxHpPer; baseStat[22] = maxMp; baseStat[23] = maxMpPer
		baseStat[24] = armor; baseStat[26] = meso; baseStat[27] = drop

		val newItemTable = ItemTable(id, itemSetCode, itemType, itemLvl, name,
			baseStat.toList(), armIgn, upGrade,
			canCube, canStarForce, maxStar, canFlame, canEnchant, canCreate)
		newItemTables.add(newItemTable)
	}

