	private val cubeMap: MutableMap<String, List<Int>> = emptyMap<String, List<Int>>().toMutableMap()
	private val abilityMap = mutableMapOf<String, Int>()
	private fun createTVOT(tableName: String, tableList: MutableList<List<String>>) {
		val typeList = List<Int>(100){0}.toMutableList()
		val valueList = List<Int>(100){0}.toMutableList()
		val oneProbList = List<Int>(100){0}.toMutableList()
		val twoProbList = List<Int>(100){0}.toMutableList()
		val threeProbList = List<Int>(100){0}.toMutableList()
		for(i in tableList.indices) {
			typeList[i] = abilityMap[tableList[i][0]]!!
			valueList[i] = tableList[i][1].toInt()
			oneProbList[i] = tableList[i][2].toInt()
			twoProbList[i] = tableList[i][3].toInt()
			threeProbList[i] = tableList[i][4].toInt()
		}
		cubeMap[tableName + "Type"] = typeList
		cubeMap[tableName + "Value"] = valueList
		cubeMap[tableName + "OneProb"] = oneProbList
		cubeMap[tableName + "TwoProb"] = twoProbList
		cubeMap[tableName + "ThreeProb"] = threeProbList
	}


