		val cubeTables = emptyList<CubeTable>().toMutableList()
		for(i in abilityOptionTypeList.indices)
			abilityMap[abilityOptionTypeList[i]] = i
		createRedCubeTable1()
		createRedCubeTable2()
		createBlackCubeTable1()
		createBlackCubeTable2()
		createAdditionalCube1()
		createAdditionalCube2()
		for(line in cubeMap) {
			cubeTables.add(CubeTable(line.key, line.value))
		}

		runBlocking {
			CoroutineScope(Dispatchers.IO).launch {
				cubeTableDao.insertAll(cubeTables)
			}.join()
		}
